  border-radius: 0 0 5px 5px;
  -webkit-box-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  border-top: none;
  color: @btn-primary-color;
  display: block;
  font-size: 12px;
  font-weight: bold;
  left: 100%;
  padding: 5px;
  position: fixed;
  top: 40%;
  transform: rotate(90deg);
  transform-origin: top left;
  text-decoration: none !important;
  white-space: nowrap;
  z-index: 300;
}

